function um_load_custom_wp_admin_style() {
    wp_register_style( 'et_icons_css', get_template_directory_uri() . '/assets/css/et-line.css', false, '1.0.0' );
    wp_enqueue_style( 'et_icons_css' );
    wp_register_style( 'et_icons_css_style', get_template_directory_uri() . '/assets/css/admin_style.css', false, '1.0.0' );
    wp_enqueue_style( 'et_icons_css_style' );
}
add_action( 'admin_enqueue_scripts', 'um_load_custom_wp_admin_style' );
function um_acf_load_et_icons( $field ){
    // reset choices
    $field['choices'] = array();

    foreach($GLOBALS["et_icons"] as $icon){
        $field['choices'][$icon['class']] = $icon["char"];
    }
    return $field;
}

